jQuery(document).ready(function($){$(".level-bar-inner").css("width","0"),$(window).on("load",function(){$(".level-bar-inner").each(function(){var n=$(this).data("level");$(this).animate({width:n},800)})})});
jQuery(document).ready(function($){$(".level-bar-inner-soft").css("width","0"),$(window).on("load",function(){$(".level-bar-inner-soft").each(function(){var n=$(this).data("level");$(this).animate({width:n},800)})})});